                                                                                                                                                                                                                                                                                                                                                        </tr>
                                                                                                                            <tr>
                                                                                                                                                                                                                        <td>Hypertrophy</td>
                                                                                                                                                                                                                                                                                                <td>3â€“4</td>
                                                                                                                                                                                                                                                                                                <td>8â€“12</td>
                                                                                                                                                                                                                                                                                                                                                        </tr>
                                                                                                                            <tr>
                                                                                                                                                                                                                        <td>Endurance Training</td>
                                                                                                                                                                                                                                                                                                <td>2â€“3</td>
