                                                                                                                                                                                                                                                                                                                                                        </tr>
                                                                                                                            <tr>
                                                                                                                                                                                                                        <td>Power Training</td>
                                                                                                                                                                                                                                                                                                <td>3â€“5</td>
                                                                                                                                                                                                                                                                                                <td>1â€“5</td>
                                                                                                                                                                                                                                                                                                                                                        </tr>
