                                                                                                                                                                                                                        <td>Hypertrophy</td>
                                                                                                                                                                                                                                                                                                <td>3â€“6</td>
                                                                                                                                                                                                                                                                                                <td>30â€“45 seconds</td>
                                                                                                                                                                                                                                                                                                                                                        </tr>
                                                                                                                            <tr>
                                                                                                                                                                                                                        <td>Endurance Training</td>
                                                                                                                                                                                                                                                                                                <td>2â€“3</td>
