                                                                                                                                                                                                                                                                                                                                                        </tr>
                                                                                                                            <tr>
                                                                                                                                                                                                                        <td>Power Training</td>
                                                                                                                                                                                                                                                                                                <td>3â€“5</td>
                                                                                                                                                                                                                                                                                                <td>5â€“15 seconds (Explosive)</td>
                                                                                                                                                                                                                                                                                                                                                        </tr>
