                                                                                                                                                                                                            </tr>
                                                                                                                            <tr>
                                                                                                                                                                                                                        <td>Hypertrophy</td>
                                                                                                                                                                                                                                                                                                <td>3–4</td>
                                                                                                                                                                                                                                                                                                                                                                                                                                            <td>45–60 seconds hold</td>
                                                                                                                                                                                                            </tr>
                                                                                                                            <tr>
                                                                                                                                                                                                                        <td>Endurance Training</td>
                                                                                                                                                                                                                                                                                                <td>2–3</td>
