                                                                                                                                                                                                                                                                                                                                                        </tr>
                                                                                                                            <tr>
                                                                                                                                                                                                                        <td>Hypertrophy</td>
                                                                                                                                                                                                                                                                                                <td>3–4</td>
                                                                                                                                                                                                                                                                                                <td>8–12</td>
                                                                                                                                                                                                                                                                                                                                                        </tr>
                                                                                                                            <tr>
                                                                                                                                                                                                                        <td>Endurance Training</td>
                                                                                                                                                                                                                                                                                                <td>3–4</td>
                                                                                                                                                                                                                                                                                                <td>15–20</td>
                                                                                                                                                                                                                                                                                                                                                        </tr>
                                                                                                                            <tr>
                                                                                                                                                                                                                        <td>Power Training</td>
                                                                                                                                                                                                                                                                                                <td>3–5</td>
